def extract_ssrc_from_ip_udp(hex_traffic):
    # IP 헤더와 UDP 헤더의 길이를 각각 정의
    ip_header_length = 20  # IP 헤더의 길이는 고정이므로 20바이트
    udp_header_length = 8  # UDP 헤더의 길이는 고정이므로 8바이트

    # IP 헤더와 UDP 헤더 이후의 데이터를 추출하여 RTP 패킷으로 간주
    rtp_packet = hex_traffic[ip_header_length*2 + udp_header_length*2:]  # 16진수 문자열에서 IP 헤더와 UDP 헤더 이후의 부분을 추출
    ssrc_offset = 8  # SSRC 값은 RTP 헤더의 4번째 워드부터 시작하므로 오프셋은 8바이트
    ssrc_length = 4  # SSRC 값은 32비트이므로 8바이트

    # SSRC 값을 추출하여 16진수 문자열에서 10진수로 변환
    ssrc_hex = rtp_packet[ssrc_offset*2:ssrc_offset*2 + ssrc_length*2]
    print(ssrc_hex)
    ssrc_decimal = int(ssrc_hex, 16)

    return ssrc_decimal

# 예시 입력 (16진수 형태의 네트워크 트래픽)
hex_traffic = ""
ssrc_value = extract_ssrc_from_ip_udp(hex_traffic)
print("Extracted SSRC value:", ssrc_value)
